export function dateFormatter(date, tzd) {
  const tzn = new Date().getTimezoneOffset() * 60;
  let ret = (date + tzd + tzn) * 1000;
  return ret;
}

export function iconFormatter(weather) {
  const { id, icon } = weather;
  switch (id) {
    case 200:
    case 201:
    case 202:
    case 230:
    case 231:
    case 232:
      return "weather-lightning-rainy";

    case 210:
    case 211:
    case 212:
    case 221:
      return "weather-lightning";

    case 300:
    case 301:
    case 302:
      return "weather-partly-rainy";

    case 310:
    case 311:
    case 312:
    case 313:
    case 314:
    case 321:
      return "weather-rainy";

    case 310:
    case 311:
    case 312:
    case 313:
    case 314:
    case 310:
    case 311:
    case 312:
    case 313:
    case 314:
    case 321:
      return "weather-rainy";

    default:
      break;
  }
}
