import React, { useState, useEffect, useContext } from "react";
import { ImageBackground, ScrollView, RefreshControl } from "react-native";
import Report from "../components/Report";
import Header from "../components/Header";
import { Context } from "../context/Context";

export default function HomeScreen() {
  const [refreshing, setRefreshing] = useState(false);
  const {
    city,
    dataFromCity,
    active,
    loading,
    error,
    weatherData,
  } = useContext(Context);
  useEffect(() => {
    eff();
  }, [city]);

  const eff = () => {
    setRefreshing(true);
    dataFromCity(city);
    setRefreshing(false);
  };

  return (
    <ImageBackground
      source={{ uri: "http://picsum.photos/720/540?blur=1" }}
      style={{ height: "100%", width: "100%", opacity: 0.7 }}
    >
      <Header />
      <ScrollView
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={eff} />
        }
      >
        <Report weatherData={weatherData[active]} loading error />
      </ScrollView>
    </ImageBackground>
  );
}
