import React, { useContext } from "react";
import { StyleSheet, View, Text, Image, ActivityIndicator } from "react-native";
import colors from "../config/colors";
import { Context } from "../context/Context";
import Wind from "./Wind";

const Report = async () => {
  const { active, loading, error, weatherData } = useContext(Context);
  const res = await weatherData[active];
  console.log(res);
  const { data, loc } = res;
  return (
    <>
      {loading ? (
        <ActivityIndicator animating={loading} />
      ) : (
        <>
          {error && <Text>No such location!</Text>}

          {data && (
            <View style={styles.container}>
              <Text style={styles.location}>
                {loc.city}, {loc.country}
              </Text>
              <Image
                style={styles.image}
                source={{
                  uri: `http://openweathermap.org/img/wn/${data.current.weather[0].icon}.png`,
                }}
              />
              <Text style={styles.h3}>{data.current.weather[0].main}</Text>
              <Text style={styles.h1}>
                {Math.round(data.current.temp - 273)}°C
              </Text>
              <View style={styles.times}>
                <Text style={styles.h4}>
                  Sunrise {" \n"}
                  {new Date(data.current.sunrise * 1000).getHours()}:
                  {new Date(data.current.sunrise * 1000).getMinutes()}
                </Text>
                <View style={styles.divider} />
                <Text style={styles.h4}>
                  Sunset{" \n"}
                  {new Date(data.current.sunset * 1000).getHours()}:
                  {new Date(data.current.sunset * 1000).getMinutes()}
                </Text>
              </View>
              <Wind deg={data.wind_deg} speed={data.wind_speed} />
              <Text>{JSON.stringify(new Date(data.current.dt * 1000))}</Text>
            </View>
          )}
        </>
      )}
    </>
  );
};

export default Report;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    textAlign: "center",
    justifyContent: "center",
    height: "100%",
    width: "100%",
  },

  h1: {
    fontSize: 60,
    color: colors.text,
  },
  h3: {
    fontSize: 40,
    color: colors.text,
  },
  h4: {
    fontSize: 26,
    color: colors.text,
  },
  location: {
    fontSize: 40,
    color: colors.text,
    fontWeight: "600",
  },
  image: {
    height: 90,
    width: 90,
  },
  times: {
    flexDirection: "row",
    alignItems: "center",
  },
  divider: {
    borderWidth: 1,
    borderColor: "#bbb",
    marginRight: 20,
    height: 80,
  },
});
