import settings from "../config/settings";
import apiClient from "./client";

const getCity = async (city) => {
  try {
    const response = await apiClient.get(
      `weather?q=${city}&appid=${settings.apiKey}`
    );
    return response.data;
  } catch (err) {
    console.log(err);
    return;
  }
};

const getCoords = (lat, lon) =>
  apiClient.get(`weather?lat=${lat}&lon=${lon}&appid=${settings.apiKey}`);

const getPredictionCity = (city) =>
  //no such request on api
  //will be removed
  apiClient.get(`onecall?q=${city}&appid=${settings.apiKey}`);

const getPredictionCoords = (lat, lon) =>
  apiClient.get(`onecall?lat=${lat}&lon=${lon}&appid=${settings.apiKey}`);

export default { getCity, getCoords, getPredictionCity, getPredictionCoords };
