import React from "react";
import { Image, StyleSheet, Text, View } from "react-native";
import colors from "../config/colors";

const Item = ({ item, type }) => {
  return (
    <View style={styles.container}>
      <Image
        style={styles.icon}
        source={{
          uri: `http://openweathermap.org/img/wn/${item.weather[0].icon}.png`,
        }}
      />
      <Text style={styles.temp}>{Math.round(item.temp - 273)}Â°C</Text>
      {type == "hourly" ? (
        <Text style={styles.time}>
          {new Date(item.dt * 1000).getHours()}:
          {new Date(item.dt * 1000).getMinutes()}
        </Text>
      ) : (
        <Text style={styles.time}>
          {new Date(item.dt * 1000).getDate()}:
          {new Date(item.dt * 1000).getMonth()}
        </Text>
      )}
    </View>
  );
};

export default Item;

const styles = StyleSheet.create({
  container: {
    height: 120,
    width: 90,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: colors.dark,
    borderRadius: 10,
    paddingVertical: 10,
    margin: 10,
  },
  icon: {
    height: 30,
    width: 30,
    padding: 10,
  },
  temp: {
    fontSize: 16,
    fontWeight: "bold",
    color: colors.text,
    paddingHorizontal: 5,
  },
  time: {
    fontSize: 14,
    color: colors.text,
    paddingHorizontal: 5,
  },
});
