import React, { useContext } from "react";
import {
  StyleSheet,
  View,
  Text,
  Image,
  ImageBackground,
  ActivityIndicator,
} from "react-native";
import colors from "../config/colors";
import { Context } from "../context/Context";
import Wind from "./Wind";

function Report() {
  const { active, loading, error, weatherData } = useContext(Context);
  const { data, loc } = weatherData[active]
    ? weatherData[active]
    : { data: undefined, loc: undefined };

  var theme = "require('../assets/bg-day.jpg')";
  if (data) {
    data.current.weather[0].icon.charAt(2) == "n"
      ? console.log("night")
      : console.log("day");
    // theme =
    //   data.current.weather[0].icon[2] == "n"
    //     ? "require(`../assets/bg-night.jpg`)"
    //     : "require(`../assets/bg-day.jpg`)";
  }

  return (
    <View>
      <ImageBackground
        source={theme}
        style={{ height: "100%", width: "100%", opacity: 0.7 }}
      >
        {loading ? (
          <ActivityIndicator animating={loading} />
        ) : (
          <View>
            {error && <Text>No such location!</Text>}

            {data && (
              <View style={styles.container}>
                <Text style={styles.location}>
                  {loc.city}, {loc.country}
                </Text>
                <Image
                  style={styles.image}
                  source={{
                    uri: `http://openweathermap.org/img/wn/${data.current.weather[0].icon}.png`,
                  }}
                />
                <Text style={styles.h3}>{data.current.weather[0].main}</Text>
                <Text style={styles.h1}>
                  {Math.round(data.current.tempC - 273)}°C
                </Text>
                <Text style={styles.h4}>
                  Feels Like : {Math.round(data.current.feel - 273)}°C
                </Text>
                <View style={styles.times}>
                  <Text style={styles.h4}>
                    Sunrise {" \n"}
                    {new Date(data.current.sunrise * 1000).getHours()}:
                    {new Date(data.current.sunrise * 1000).getMinutes()}
                  </Text>
                  <View style={styles.divider} />
                  <Text style={styles.h4}>
                    Sunset{" \n"}
                    {new Date(data.current.sunset * 1000).getHours()}:
                    {new Date(data.current.sunset * 1000).getMinutes()}
                  </Text>
                </View>
                <Wind
                  deg={data.current.wind_deg}
                  speed={data.current.wind_speed}
                />
                <Text>{JSON.stringify(new Date(data.current.dt * 1000))}</Text>
              </View>
            )}
          </View>
        )}
      </ImageBackground>
    </View>
  );
}

export default Report;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    textAlign: "center",
    justifyContent: "center",
    height: "100%",
    width: "100%",
  },

  h1: {
    fontSize: 40,
    color: colors.text,
  },
  h3: {
    fontSize: 30,
    color: colors.text,
  },
  h4: {
    fontSize: 20,
    color: colors.text,
  },
  location: {
    fontSize: 20,
    color: colors.text,
    fontWeight: "600",
  },
  image: {
    height: 70,
    width: 70,
  },
  times: {
    flexDirection: "row",
    alignItems: "center",
  },
  divider: {
    borderWidth: 1,
    borderColor: "#bbb",
    marginRight: 20,
    height: 80,
  },
});
