import React, { useContext } from "react";
import {
  StyleSheet,
  Text,
  FlatList,
  ScrollView,
  TouchableHighlight,
  View,
} from "react-native";
import colors from "../config/colors";
import { Context } from "../context/Context";

const MenuScreen = () => {
  const { list, setActive } = useContext(Context);
  return (
    <View style={styles.container}>
      <ScrollView>
        {list.length > 0 && <Text style={styles.title}>Locations</Text>}
        <FlatList
          data={list}
          keyExtractor={(item, index) => item + index}
          renderItem={({ item }) => (
            <TouchableHighlight
              style={styles.item}
              onPress={() => {
                setActive(item);
              }}
            >
              <Text>{item}</Text>
            </TouchableHighlight>
          )}
        />
      </ScrollView>
    </View>
  );
};

export default MenuScreen;

const styles = StyleSheet.create({
  container: {
    minHeight: 200,
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    color: colors.text,
  },
  item: {
    backgroundColor: "#f9c2ff",
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
});
