import React, { useEffect, useContext, useState } from "react";
import { StyleSheet, View, Text, Image, ActivityIndicator } from "react-native";
import useApi from "../api/useApi";
import api from "../api/weather";
import colors from "../config/colors";
import { Context } from "../context/Context";

function Report() {
  const [pip, setpip] = useState(false);
  useEffect(() => {
    eff(city);
  }, [city]);
  const { loading, error, cityData, city, getCity } = useContext(Context);

  const eff = async (city) => {
    await getCity(city);
    console.log("effect");
  };
  //   const { data, error, loading, request } = useApi(api.getCurrent);

  return (
    <>
      {loading ? (
        <ActivityIndicator animating={loading} />
      ) : (
        <>
          {error && <Text>Error Found!!</Text>}
          <Text onPress={() => setpip(!pip)}>
            {loading}+{error}+{pip ? "Yes" : "No"}
          </Text>
          {cityData && (
            <View style={styles.container}>
              <Text style={styles.location}>
                {cityData.name}, {cityData.sys.country}
              </Text>
              <Image
                style={styles.image}
                source={{
                  uri: `http://openweathermap.org/img/wn/${cityData.weather[0].icon}.png`,
                }}
              />
              <Text style={styles.h3}>{cityData.weather[0].description}</Text>
              <Text style={styles.h1}>
                {Math.round(cityData.main.temp - 273)}Â°C
              </Text>
            </View>
          )}
        </>
      )}
    </>
  );
}

export default Report;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: colors.dark,
    height: "100%",
    width: "100%",
  },
  h1: {
    fontSize: 60,
    color: colors.text,
  },
  h3: {
    fontSize: 40,
    color: colors.text,
  },
  location: {
    fontSize: 40,
    color: colors.text,
    fontWeight: "600",
  },
  image: {
    height: 90,
    width: 90,
  },
});
