import weatherApi from "../api/weather";

export const SET_CITY = "SET_CITY";
export const GET_CITY_DATA = "GET_CITY_DATA";
export const GET_PREDICTION_CITY = "GET_PREDICTION_CITY";
export const SET_COORDS = "GET_COORDS";
export const GET_COORDS_DATA = "GET_COORDS_DATA";
export const GET_PREDICTION_COORDS = "GET_PREDICTION_COORDS";
export const ERROR = "ERROR";

export const createActions = (dispatch) => ({
  setCity: (formData) => {
    dispatch({
      type: SET_CITY,
      payload: formData,
    });
    getCity();
  },
  setCoords: ({ lat, lon }) => {
    try {
      //Get Coords from phone
      dispatch({
        type: SET_COORDS,
        payload: { lat, lon }, //coords lat, lon
      });
    } catch (error) {
      console.log(error);
      //Error
      dispatch({
        type: ERROR,
        payload: "Couldn't get current location of your device",
      });
    }
  },
  getCity: async (loc) => {
    try {
      const wData = await weatherApi.getCity(loc);
      dispatch({
        type: GET_CITY_DATA,
        payload: wData,
      });
    } catch (error) {
      console.log(error);
      dispatch({
        type: ERROR,
        payload: "Error",
      });
    }
  },
  getCoords: async ({ lat, lon }) => {
    try {
      const wData = await weatherApi.getCoords({ lat, lon });
      dispatch({
        type: GET_COORDS_DATA,
        payload: wData.data,
      });
    } catch (error) {
      console.log(error);
      dispatch({
        type: ERROR,
        payload: "Error",
      });
    }
  },
  getPredictionCity: async (loc) => {
    try {
      const wData = await weatherApi.getCityPrediction(loc);
      dispatch({
        type: GET_PREDICTION_CITY,
        payload: wData.data,
      });
    } catch (error) {
      console.log(error);
      dispatch({
        type: ERROR,
        payload: "Error",
      });
    }
  },
  getPredictionCoords: async ({ lat, lon }) => {
    try {
      const wData = await weatherApi.getPredictionCoords({ lat, lon });
      dispatch({
        type: GET_PREDICTION_COORDS,
        payload: wData.data,
      });
    } catch (error) {
      console.log(error);
      dispatch({
        type: ERROR,
        payload: "Error",
      });
    }
  },
});
