  useEffect(() => {
    cityData = getCity(city);
  }, []);

  //   const { data, error, loading, request } = useApi(api.getCurrent);

  const { loading, error, cityData: data, city, getCity } = useContext(Context);
  return (
    <>
      {loading ? (
        <ActivityIndicator animating={loading} />
      ) : (
        <>
          {error && <Text>Error Found!!</Text>}

          {data && (
            <View style={styles.container}>
              <Text style={styles.location}>
                {data.name}, {data.sys.country}
              </Text>
              <Image
                style={styles.image}
                source={{
                  uri: `http://openweathermap.org/img/wn/${data.weather[0].icon}.png`,
                }}
              />
              <Text style={styles.h3}>{data.weather[0].description}</Text>
              <Text style={styles.h1}>
                {Math.round(data.main.temp - 273)}Â°C
              </Text>
            </View>
          )}
        </>
      )}
    </>
  );
}

export default Report;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: colors.dark,
    height: "100%",
    width: "100%",
  },
  h1: {
    fontSize: 60,
    color: colors.text,
  },
  h3: {
    fontSize: 40,
    color: colors.text,
  },
  location: {
    fontSize: 40,
    color: colors.text,
    fontWeight: "600",
  },
