import React, { useContext } from "react";
import {
  StyleSheet,
  Text,
  FlatList,
  ScrollView,
  TouchableHighlight,
} from "react-native";
import { Context } from "../context/Context";

const MenuScreen = () => {
  const { list, setActive } = useContext(Context);
  return (
    <ScrollView style={styles.container}>
      <FlatList
        data={list}
        keyExtractor={(item, index) => item + index}
        renderItem={({ item }) => (
          <TouchableHighlight
            style={styles.item}
            onPress={(item) => setActive(item)}
          >
            <Text>{item}</Text>
          </TouchableHighlight>
        )}
      />
    </ScrollView>
  );
};

export default MenuScreen;

const styles = StyleSheet.create({
  container: {
    minHeight: 200,
  },
  item: {
    backgroundColor: "#f9c2ff",
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
});
