import {
  SET_CITY,
  SET_COORDS,
  SET_ACTIVE,
  DATA_CITY,
  DATA_COORD,
  LIST_LOCATIONS,
  ERROR,
} from "./actions";

export default function (state, action) {
  const { type, payload } = action;
  switch (type) {
    case LIST_LOCATIONS:
      return {
        ...state,
        list: Object.keys(state.weatherData),
        loading: false,
      };

    case SET_ACTIVE:
      return {
        ...state,
        active: payload,
        loading: true,
      };
    case SET_CITY:
      return {
        ...state,
        city: payload,
        loading: false,
      };
    case SET_COORDS:
      return {
        ...state,
        coords: { lat: payload.lat, lon: payload.lon },
        loading: false,
      };
    case DATA_CITY:
    case DATA_COORD:
      return {
        ...state,
        weatherData: {
          ...state.weatherData,
          [payload.loc.city]: { data: payload.data, loc: payload.loc },
        },
        loading: false,
      };

    case ERROR: {
      return {
        ...state,
        loading: false,
        error: true,
        errors: payload,
      };
    }
    default:
      return state;
  }
}
