import React, { useContext } from "react";
import { ScrollView, ImageBackground, FlatList } from "react-native";
import { Context } from "../context/Context";
import Item from "../components/Item";

const PredictionScreen = () => {
  const { weatherData, active } = useContext(Context);
  const data = weatherData[active] ? weatherData[active].data : "";
  console.log(data);
  return (
    // <ImageBackground
    //   source={{ uri: "http://picsum.photos/720/540?blur=1" }}
    //   style={{ height: "100%", width: "100%", opacity: 0.7 }}
    // >
    <>
      {data && (
        <ScrollView>
          <FlatList
            data={data.hourly}
            renderItem={({ item }) => <Item item={item} />}
            keyExtractor={(item, index) => item + index}
          />
        </ScrollView>
      )}
    </>
    // </ImageBackground>
  );
};
export default PredictionScreen;
