import React, { useContext } from "react";
import { StyleSheet, View, Text, Image, ActivityIndicator } from "react-native";
import colors from "../config/colors";
import { Context } from "../context/Context";
import Wind from "./Wind";

function Report() {
  const { loading, error, cityData } = useContext(Context);
  let sunrise, sunset;
  const formatTime = () => {
    sunrise = new Date(cityData.sys.sunrise * 1000);
    sunset = new Date(cityData.sys.sunset * 1000);
  };
  formatTime();
  return (
    <>
      {loading ? (
        <ActivityIndicator animating={loading} />
      ) : (
        <>
          {error && <Text>No such location!</Text>}

          {cityData && (
            <View style={styles.container}>
              <Text style={styles.location}>
                {cityData.name}, {cityData.sys.country}
              </Text>
              <Image
                style={styles.image}
                source={{
                  uri: `http://openweathermap.org/img/wn/${cityData.weather[0].icon}.png`,
                }}
              />
              <Text style={styles.h3}>{cityData.weather[0].description}</Text>
              <Text style={styles.h1}>
                {Math.round(cityData.main.temp - 273)}Â°C
              </Text>
              <Text style={styles.h3}>
                Sunrise {" \n"}
                {cityData.sys.sunrise}
              </Text>
              <Text style={styles.h3}>
                Sunset{" \n"}
                {cityData.sys.sunset}
              </Text>
              <Wind deg={cityData.wind.deg} speed={cityData.wind.speed} />
            </View>
          )}
        </>
      )}
    </>
  );
}

export default Report;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: colors.dark,
    height: "100%",
    width: "100%",
  },
  h1: {
    fontSize: 60,
    color: colors.text,
  },
  h3: {
    fontSize: 40,
    color: colors.text,
  },
  location: {
    fontSize: 40,
    color: colors.text,
    fontWeight: "600",
  },
  image: {
    height: 90,
    width: 90,
  },
});
