import settings from "../config/settings";
import apiClient from "./client";

const getCity = async (city = "pabna") => {
  try {
    const response = await apiClient.get(
      `weather?q=${city}&appid=${settings.apiKey}`
    );
    return response.data;
  } catch (err) {
    console.log(err);
    return;
  }
};

const getCoords = (coords) =>
  // should be resolved=>
  apiClient.get(`weather?q=${coords}&appid=${settings.apiKey}`);

const getPredictionCity = (city = "pabna") =>
  // should be resolved=>
  apiClient.get(`weather?q=${city}&appid=${settings.apiKey}`);

const getPredictionCoords = (coords) =>
  // should be resolved=>
  apiClient.get(`weather?q=${coords}&appid=${settings.apiKey}`);

export default { getCity, getCoords, getPredictionCity, getPredictionCoords };
