import React, { useContext, useEffect } from "react";
import { StyleSheet, View, Text, Image, ActivityIndicator } from "react-native";
import colors from "../config/colors";
import { Context } from "../context/Context";
import Wind from "./Wind";

function Report() {
  const { loading, error, cityData: data } = useContext(Context);

  return (
    <>
      {loading ? (
        <ActivityIndicator animating={loading} />
      ) : (
        <>
          {error && <Text>No such location!</Text>}

          {data && (
            <View style={styles.container}>
              <Text style={styles.location}>
                {data.name}, {data.sys.country}
              </Text>
              <Image
                style={styles.image}
                source={{
                  uri: `http://openweathermap.org/img/wn/${data.weather[0].icon}.png`,
                }}
              />
              <Text style={styles.h3}>{data.weather[0].description}</Text>
              <Text style={styles.h1}>
                {Math.round(data.main.temp - 273)}Â°C
              </Text>
              <View style={styles.times}>
                <Text style={styles.h4}>
                  Sunrise {" \n"}
                  {new Date(data.sys.sunrise * 1000).getHours()}:
                  {new Date(data.sys.sunrise * 1000).getMinutes()}
                </Text>
                <View style={styles.divider} />
                <Text style={styles.h4}>
                  Sunset{" \n"}
                  {new Date(data.sys.sunset * 1000).getHours()}:
                  {new Date(data.sys.sunset * 1000).getMinutes()}
                </Text>
              </View>
              <Wind deg={data.wind.deg} speed={data.wind.speed} />
              <Text>{JSON.stringify(new Date(data.dt * 1000))}</Text>
            </View>
          )}
        </>
      )}
    </>
  );
}

export default Report;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    textAlign: "center",
    justifyContent: "center",
    height: "100%",
    width: "100%",
  },

  h1: {
    fontSize: 60,
    color: colors.text,
  },
  h3: {
    fontSize: 40,
    color: colors.text,
  },
  h4: {
    fontSize: 26,
    color: colors.text,
  },
  location: {
    fontSize: 40,
    color: colors.text,
    fontWeight: "600",
  },
  image: {
    height: 90,
    width: 90,
  },
  times: {
    flexDirection: "row",
    alignItems: "center",
  },
  divider: {
    borderWidth: 1,
    borderColor: "#bbb",
    marginRight: 20,
    height: 80,
  },
});
